mapper.select:
SELECT *
FROM link_instances
WHERE link_type_id = #{linkTypeId}
  AND source_instance_id = #{sourceInstanceId}

SELECT *
FROM link_instances
WHERE link_type_id = #{linkTypeId}
  AND target_instance_id = #{targetInstanceId}

SELECT *
FROM link_instances
WHERE source_instance_id = #{instanceId}
   OR target_instance_id = #{instanceId}
